import React, { createContext, useState } from 'react';
import Events from './Pages/Events/Events';

import './App.css';
import { makeStyles } from '@material-ui/core/styles';

import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom'
import EventConfirmation from "./components/AddEvents/Confirmation";
import CoachingConfirmation from "./components/AddCoaching/Confirmation";
// import qs from 'querystring';

const useStyles = makeStyles({
  appWindow: {
    height: "100%",
    width: "calc(100% - 100px)"
  },
  mainContainer: {
    display: "flex",
    overflowY: "auto",
    height: "calc(100% - 80px)",
    width: "100%"
  },
  innerContainer: {
    display: "flex",
    margin: "0 auto",
    maxWidth: "1180px",
    width: "100%",
    height: "100%"
  }
});

function App() {
  const [PageTitle, setPageTitle] = useState("AFS");
  const [navTitle, setNavTitle] = useState('');
  const classes = useStyles();
  // useEffect(() => {
