  //     headers: {
  //       'x-api-key': 'd25c0fb9-f7a3-452d-bea3-098450034ac0',
  //       'Authorization': 'Basic Qzg2MjE0MzZjZDc5YzE3NWVlYjA5NzFhNWRiODhmZDU2MDliZThjZTA6U1lETWNlMHRsWXl6Z1IxWkdaTGQ5S2xobXRLQkdKejJicG9FajhPZUFZNnMwblBmVGJ1VHR5cVBjeEtpa1pQag==',
  //       'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8',
  //     },
  //   });
  //   instance.post('', qs.stringify(requestBody)).then(res => console.log('response', res));
  // }, []);

  return (
    <ErrorBoundary>
      <BrowserRouter>
        <Sidebar />
        <div className={classes.appWindow}>
          <Navbar
            path="Decathlon > Bangalore > Bannerghata >"
            title= {navTitle}
          />
          <div className={classes.mainContainer}>
            <div className={classes.innerContainer}>
              <Switch>
                <Route exact path="/events" component={Events} />
                <Route exact path="/coaching" component={Coaching} />
                <Route exact path="/facilty" component={Facility} />
                <Route
                  exact
                  path="/event/add/confirmation"
                  component={props => <EventConfirmation />}
                />
                <Route path="/event/add" component={()=> { setNavTitle('ADD EVENT'); return < AddEvent />}} />
                <Route
                  exact
                  path="/coaching/add/confirmation"
                  component={props => <CoachingConfirmation />}
                />
                <Route path="/coaching/add" component={()=> { setNavTitle('ADD COACHING'); return <AddCoaching /> }} />
                <Route path="/facility/add" component={()=> {setNavTitle('ADD FACILITY');return < AddFacility />}} />
                <Route
                  exact
                  path="*"
                  render={() => <Redirect to="/event/add/basic" />}
                />
              </Switch>
            </div>
          </div>
        </div>
      </BrowserRouter>
    </ErrorBoundary>
  );
}

export default App;
