import React, { useState, useEffect }  from 'react'
import InputField from './InputField/InputField'
import { makeStyles } from '@material-ui/core/styles';
import Button from './Button/Button'
import Header from './Header/Header'

const useStyles = makeStyles({
    container: {
        width: '100%',
    },
    mB: {
        marginBottom: '40px',
    },
    mB2: {
        marginBottom: '20px',
    },
    width: {
        width: '240px',
    },
    picker: {
        display: 'flex',
        justifyContent: 'space-between',
    },
});

const styles = {
    left: {
      height: "250px",
      position: "sticky",
      top: 0,
      width: "250px",
      display: "flex",
      flexDirection: "column",
      alignItems: "center"
    },
    mid: {
      width: "100%",
      height: "100%",
    },
    back: {
      marginLeft: "5.5px",
      marginTop: "40px",
      marginBottom: "40px"
    }
  };
const Login  = props =>{

const [username, setUserName] = useState('');
const [password,setpassword]=useState('');

    const classes = useStyles();

    const onNextHandler = () => {

      const payload = {
          username,
          password,

      }
      console.log('Details Login',payload)

  };

    return (
        <>
        <div style={styles.left}>
        <div>
          <Button
            text
            style={styles.back}
            icon={<i className="fas fa-arrow-left" />}>
          </Button>
        </div>
      </div>

            {/* <Header title="Let’s start" fontSize="36px" style={{ marginTop: '80px', marginBottom: '40px' }} />

                <div className={classes.mB2}>
                    <InputField value={username} label="User Name" onChange={setUserName} />
                </div>
                <div className={classes.mB}>
                    <InputField value={password}  label="Password" onChange={setpassword} />
                </div>
                <div className={classes.mB}>
                <Button value="Submit" large onClick={onNextHandler} right icon={<i className="fas fa-greater-than"></i>} />
               </div> */}
               <div className="events-container">
                <h2>Add New Ticket</h2>

                <Grid container spacing={3}>
                    <Grid item xs={7}>
                        <div className="py-2">
                            <InputField label="Ticket Name" placeholder="Ex: Early Bird, Regular, Express..." onChange={this.setName.bind(this)} />
                            <p onClick={ this.showDiscription.bind(this) }>Add Description</p>
                        </div>
                        <div className="py-2" className={showDescription ? "d-block" : "d-none"}>
                            <InputField label="Description" onChange={this.setDescription}/>
                        </div>
                        <div className="py-2">
                            <label className="field-name">Ticket Type</label>
                            <Selector list={["PAID","FREE"]} selectHandler={this.toggle} />
                        </div>
                        <div className={paidFreeToggle ? "d-block" : "d-none"}>
                            <Grid container spacing={2} className="py-2">
                                <Grid item xs={6} className="position-relative">
                                    <InputField label="Ticket Price (in INR)" onChange={this.setTicketPrice.bind(this)}/>
                                    <span className="currency">&#8377;</span>
                                </Grid>
                                <Grid item xs={6} className="position-relative">
                                    <InputField label="You’ll receive" disabled onChange={this.basePrice.bind(this)}/>
                                    <span className="currency">&#8377;</span>
                                    <span className="view-breakup">View Breakup</span>
                                </Grid>
                            </Grid>
                        </div>
                        <Grid container spacing={2} className="py-2">
                            <Grid item xs={6}>
                                <InputField label="Total No. of Tickets" onChange={this.setTotalTickets.bind(this)}/>
                            </Grid>
                            <Grid item xs={6}>
                                <InputField label="Max. No. of Tickets / Booking" onChange={this.setmaxTickets.bind(this)}/>
                            </Grid>
                        </Grid>
                        <Grid container spacing={2} className="py-2">
                            <Grid item xs={6}>
                                <Button value="Cancel" large={true} secondary/>
                            </Grid>
                            <Grid item xs={6}>
                                <Button value="+ Add" large={true} primary onClick={this.onSubmit.bind(this)}/>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>

               {/* <Popup /> */}

            </div>


        </>
);


};

export default Login
