import React, { useState, useEffect } from 'react'
import InputField from './InputField/InputField'
import { makeStyles } from '@material-ui/core/styles';
import Button from './Button/Button'
import Header from './Header/Header'

const useStyles = makeStyles({
  container: {
    width: '100%',
  },
  mB: {
    marginBottom: '40px',
  },
  mB2: {
    marginBottom: '20px',
  },
  width: {
    width: '240px',
  },
  picker: {
    display: 'flex',
    justifyContent: 'space-between',
  },
});

const styles = {
  left: {
    height: "250px",
    position: "sticky",
    top: 0,
    width: "250px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center"
  },
  mid: {
    width: "100%",
    height: "100%",
  },
  back: {
    marginLeft: "5.5px",
    marginTop: "40px",
    marginBottom: "40px"
  }
};
const Login = props => {

  const [email, setEmail] = useState('');
  const [password, setpassword] = useState('');

  const classes = useStyles();

  const onNextHandler = () => {

    const payload = {
      email,
      password,

    }
    console.log('Details Login', payload)

  };

  const validateEmail = () => {
    const pattern = /^\w+@[a-zA-Z_]+?\.[a-zA-Z]{2,3}$/;
    let emailErr = "";
    if (email.length === 0) {
      emailErr = "Please Enter eMail id";
    } else if (!pattern.test(email)) {
      emailErr = "Please enter valid email id";
    }
    return {
      isValid: emailErr === "" ? true : false,
      err: emailErr
    };
  };

  return (
    <>
      <div style={styles.left}>
        <div>
          <Button
            text
            style={styles.back}
            icon={<i className="fas fa-arrow-left" />}>
          </Button>
        </div>
      </div>
      <div className={styles.container}>
        <Header title="Let’s start" fontSize="36px" style={{ marginTop: '80px', marginBottom: '40px' }} />

        <div className={classes.mB2}>
          <InputField 
          onChange={setEmail}
          value={email}
          error={err.errorMsg.email ? err.errorMsg.email.length : false}
          errorMsg={err.errorMsg && err.errorMsg.email}
          label="Email"
            />
        </div>
        <div className={classes.mB}>
          <InputField type="password" value={password} label="Password" onChange={setpassword} />
        </div>
        <div className={classes.mB}>
          <Button value="Submit" large onClick={onNextHandler} right icon={<i className="fas fa-greater-than"></i>} />
        </div>
      </div>

    </>
  );


};

export default Login
